import React from 'react';
import '../App.css';
import '../HomePage.css';

export default class HomePage extends React.Component{

    componentWillMount(){
        console.log("Will Mount")
        console.log(this.props)
        this.props.getSports(this.props.user);
        this.props.getActivities(this.props.user);
    }

    render(){
        console.log("Home render")
        console.log(this.props.sports);
        const sports = this.props.sports;
        console.log(this.props.activities);
        const activities = this.props.activities;
        return (
            <div className="App">
                <h1>
                    Home
                </h1>
                <h2>
                    My activities
                </h2>
                <table className="tableCustom" align="center">
                    <tr className="tableTitleCustom">
                        <th className="tableTdCustom">
                            <strong>Sport</strong>
                        </th>
                        <th className="tableTdCustom">
                            <strong>Localisation</strong>
                        </th>
                    </tr>
                    {activities.map(activity =>
                    <tr>
                        <td className="tableTdCustom">
                            {activity.sport.name}
                        </td>
                        <td className="tableTdCustom">
                            {activity.localisation.ville}
                        </td>
                    </tr>
                    )}
                </table>
                <footer>

                </footer>
            </div>
        )
    }
}
